h1,
h2,
h3,
h4,
h5,
h6,
p,
ul {
  margin: 0;
}

ul {
  padding-left: 0;
}

.container {
  width: 1440px;
  margin: 0 auto;
}
body {
  background-color: #363F46;
}
.link {
  text-decoration: none;
}
.list {
  list-style: none;
}

.btn {
font-family: 'DM Sans';
font-style: normal;
font-weight: 400;
font-size: 18px;
line-height: 1.59;

color: #FFFFFF;
padding: 12px 15px 12px 20px;
background-color: #118DA8;
border: none;
border-radius: 4px;
}

.splash {
  width: 68px;
  height: 74px;
  background: transparent;
}

