.header__container,
.header__nav,
.nav__list {
    display: flex;
    
}
.header__container {
    padding-left: 50px;
    padding-right: 70px;
}


.header__nav {
    margin-right: 0;
    margin-left: auto;
}

.nav__list {
    align-items: center;
    gap: 40px;
}
.nav__link {
font-family: 'DM Sans', serif;
font-style: normal;
font-weight: 400;
font-size: 17px;
line-height: 1.59;
color: #FFFFFF;

transition: color 250ms cubic-bezier(0.4, 0, 0.2, 1);;
&--active,
&:hover,
&:focus {
color: #118DA8;
}
}

