.hero__section {
  display: flex;
  padding: 0 50px;
}

.thumb {
    position: relative;
}
.splash__hero {
    position: absolute;
    top: -25px;
    left: -30px;
    rotate: 110deg;
    
    z-index: 1;
}
.hero__img {
  display: block;
  width: 650px;
  border-radius: 20px;
  
}

.hero__header {
  display: block;
  margin-top: 294px;
  margin-bottom: 153px;
  margin-right: 114px;
  margin-left: 60px;
}
.header__text {
  font-family: "DM Sans", serif;
  font-style: normal;
  font-weight: 400;
  font-size: 70px;
  line-height: 1.26;
  letter-spacing: 3.2px;

  color: #ffffff;
}
.header__first__enlargement {
  position: relative;
  display: inline-block;
  font-family: "DM Sans", serif;
  font-style: normal;
  font-weight: 400;
  font-size: 75px;
  line-height: 1.17;

  letter-spacing: 3.2px;

  color: #ffffff;

  margin-right: 25px;
}

.text__underline {
  position: absolute;
  left: 0;
  top: 10px;
  width: 100%;
}

.header__second__enlargement {
  font-family: "DM Sans", serif;
  font-style: normal;
  font-weight: 700;
  font-size: 80px;
  line-height: 1.1;

  letter-spacing: 3.2px;

  color: #ffffff;
}
.hero__socials {
    margin-top: 74px;
    padding-left: 22px;
}
.hero__paragraph {
  font-family: "DM Sans";
  font-style: normal;
  font-weight: 400;
  font-size: 15px;
  line-height: 1.6;

  color: #ffffff;
  margin-bottom: 16px;
}
.music__socials {
  display: flex;

  gap: 17px;
  
}

.music__socials__icon {
  width: 32px;
  height: 32px;
  transition: transform 250ms cubic-bezier(0.4, 0, 0.2, 1);

&:hover,
&:focus {
transform: scale(1.5);
}
}

